const form=document.getElementById("contact_form"),spinner=document.getElementById("spinner");function setSpinner(e){e?(spinner.classList.remove("hidden"),spinner.classList.add("inline")):(spinner.classList.add("hidden"),spinner.classList.remove("inline"))}function setContactMessageStatus(e){const t=document.getElementById("contact_success_text"),n=document.getElementById("contact_error_text");e?(t.classList.remove("hidden"),n.classList.add("hidden")):(t.classList.add("hidden"),n.classList.remove("hidden"))}form.addEventListener("submit",function(e){e.preventDefault(),setSpinner(!0);const t=document.getElementById("name").value,n=document.getElementById("company").value,s=document.getElementById("email").value,o=document.getElementById("message").value,i={name:t,company:n,email:s,message:o},a=form.getAttribute("action"),r={method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(i)};fetch(a,r).then(e=>{setSpinner(!1),e.ok?(form.reset(),setContactMessageStatus(!0)):setContactMessageStatus(!1)}).catch(e=>{setSpinner(!1),setContactMessageStatus(!1)})})